import { Component } from '@angular/core';
import { Country } from '../../interfaces/country';
import { CountriesService } from '../../services/countries.service';

@Component({
  selector: 'app-by-capital-page',
  templateUrl: './by-capital-page.component.html',
  styles: [
  ]
})
export class ByCapitalPageComponent {

  public countries: Country[] = [];


  constructor(private countriesService: CountriesService) {}

  searchByCapital( tern: string ):void{
    this.countriesService.searchCapital(tern).subscribe( countries => {this.countries = countries});
  }

}
<h2>Por Capital</h2>
<hr>
<div class="row">
  <div class="col">
      <!-- Search Box -->
      <share-search-box placeHolder="Buscar por Capital" (onValue) ="searchByCapital($event)" ></share-search-box>
  </div>
</div>
<div class="row">
  <div class="col">
      <hr>
        <countries-table [countries]="countries"></countries-table>
  </div>
</div>
