import { Component } from '@angular/core';
import { Country } from '../../interfaces/country';
import { CountriesService } from '../../services/countries.service';

@Component({
  selector: 'app-by-region-page',
  templateUrl: './by-region-page.component.html',
  styles: [
  ]
})
export class ByRegionPageComponent {

  public countries: Country[] = [];


  constructor(private countriesService: CountriesService) {}

  searchByRegion( tern: string ):void{
    this.countriesService.searchRegion(tern).subscribe( countries => {this.countries = countries});
  }

}
<h2>Por Capital</h2>
<hr>
<div class="row">
  <div class="col">
      <!-- Search Box -->
      <share-search-box placeHolder="Buscar por Region" (onValue) ="searchByRegion($event)" ></share-search-box>
  </div>
</div>
<div class="row">
  <div class="col">
      <hr>
        <countries-table [countries]="countries"></countries-table>
  </div>
</div>
